INSERT INTO `osoblje` (`id`, `firstName`, `lastName`, `title`, `rank`, `email`, `cv`, `image`, `lang`, `description`, `active`, `status`, `sufix`, `imageID`, `reference`, `translate_relation`) VALUES
(10, 'Албина', 'Кецман', 'др', 'Професор струковних студија', 'albina.kecman@vpscacak.edu.rs', NULL, '1715465535238_1391626674.png', 'srb', '<p>ДР АЛБИНА КЕЦМАН ШУШЊАР ЈЕ МАГИСТРИРАЛА 2006. ГОДИНЕ НА ЕКОНОМСКОМ ФАКУЛТЕТУ У БЕОГРАДУ, ГДЕ ЈЕ СТЕКЛА И МАСТЕР ДИПЛОМУ ХЕЦ ПОСЛОВНЕ ШКОЛЕ ИЗ ПАРИЗА, У ОКВИРУ МЕЂУНАРОДНОГ МАГИСТАРСКОГ КУРСА ИЗ ПОСЛОВНЕ ЕКОНОМИЈЕ. ДОКТОРИРАЛА ЈЕ 2013. ГОД. НА ЕКОНОМСКОМ ФАКУЛТЕТУ У БЕОГРАДУ.</p><p>ИЗАБРАНА ЈЕ У ЗВАЊЕ ПРОФЕСОРА СТРУКОВНИХ СТУДИЈА ЗА УЖЕ НАУЧНЕ ОБЛАСТИ МЕНАЏМЕНТ И МАРКЕТИНГ И ЕКОНОМИЈА; ФИНАНСИЈЕ И БАНКАРСТВО. НАСТАВНИК ЈЕ НА ПРЕДМЕТИМА МЕНАЏМЕНТ ЉУДСКИХ РЕСУРСА, ОРГАНИЗАЦИОНО ПОНАШАЊЕ И ПРИНЦИПИ МАРКЕТИНГА.</p><p>ПОСЕДУЈЕ ПРЕКО 15 ГОДИНА РАДНОГ ИСКУСТВА, ОД ЧЕГА СКОРО 10 ГОДИНА НА ОДГОВОРНИМ РУКОВОДЕЋИМ ПОЗИЦИЈАМА.</p><p>АУТОР ЈЕ ВИШЕ СТРУЧНИХ РАДОВА ИЗ ОБЛАСТИ МЕНАЏМЕНТА И ЕКОНОМИЈЕ, КАО И КОАТОР КЊИГЕ “МЕНАЏМЕНТ ЉУДСКИХ РЕСУРСА”.</p><p>ГОВОРИ ЕНГЛЕСКИ И ШПАНСКИ ЈЕЗИК, А ПОЗНАЈЕ И ОСНОВЕ НЕМАЧКОГ ЈЕЗИКА.</p>', 1, 'nastavno', NULL, 39, NULL, NULL),
(13, 'Владимир', 'Грујић', 'др', 'Професор струковних студија', 'vladimir.grujic@vpscacak.edu.rs', '164', '1715163371618_532717362.jpg', 'srb', '<p>ДР ВЛАДИМИР ГРУЈИЋ ЈЕ ПРОФЕСОР ВИСОКЕ ПОСЛОВНЕ ШКОЛЕ СТРУКОВНИХ СТУДИЈА ИЗ БЕОГРАДА, ЗА ОБЛАСТ ДРУШТВЕНИХ НАУКА, НА ПРЕДМЕТИМА СОЦИОЛОГИЈА, МЕНАЏМЕНТА ЉУДСКИХ РЕСУРСА И ПОСЛОВНА ЕТИКА. РОЂЕН ЈЕ У ДЕРВЕНТИ 1959. ГОДИНЕ, РЕПУБЛИКА БИХ. У НОВОМ САДУ ЈЕ ЗАВРШИО ОСНОВНУ И СРЕДЊУ ТЕХНИЧКУ ШКОЛУ. УПИСАО ЈЕ СТУДИЈЕ НА ВОЈНОЈ АКАДЕМИЈИ КОПНЕНЕ ВОЈСКЕ У БЕОГРАДУ 1979. ГОДИНЕ И НА ИСТОЈ ДИПЛОМИРАО 1983. ГОДИНЕ. ПОСТДИПЛОМСКЕ СТУДИЈЕ УПИСАО ЈЕ 1988. ГОДИНЕ НА ВОЈНОЈ АКАДЕМИЈИ НА СМЕРУ ВОЈНЕ СОЦИОЛОГИЈЕ И ИСТЕ ЗАВРШИО 1993. ГОДИНЕ. ДОКТОРСКУ ДИСЕРТАЦИЈУ НА ТЕМУ “ОРГАНИЗАЦИЈА ВОЈНЕ АКАДЕМИЈЕ ВОЈСКЕ ЈУГОСЛАВИЈЕ И МОТИВАЦИЈА СТУДЕНАТА ЗА УЧЕЊЕ”, – ОДБРАНИО ЈЕ 1999. ГОДИНЕ ИЗ ОБЛАСТИ ВОЈНЕ СОЦИОЛОГИЈЕ НА КАТЕДРИ ДРУШТВЕНИХ НАУКА ВОЈНЕ АКАДЕМИЈЕ У БЕОГРАДУ И СТЕКАО СТЕПЕН ДОКТОРА ВОЈНЕ СОЦИОЛОГИЈЕ. КАО АСИСТЕНТ ПРИПРАВНИК, КАРИЈЕРУ ЗАПОЧИЊЕ 1990. ГОДИНЕ НА ВОЈНОЈ АКАДЕМИЈИ У КАТЕДРИ ДРУШТВЕНИХ НАУКА, ГДЕ ОД 1993. ДО 1999. ГОДИНЕ РАДИ КАО АСИСТЕНТ. НАКОН ОДБРАНЕ ДОКТОРАТА НА ИСТОЈ КАТЕДРИ РАДИ КАО НАСТАВНИК ОД 1999. ДО 2001. ГОДИНЕ. ОД 2001. ДО 2007. ГОДИНЕ НАЧЕЛНИК ЈЕ КАТЕДРЕ ДРУШТВЕНИХ НАУКА, А ОД 2007. ГОДИНЕ КАО ВАНРЕДНИ ПРОФЕСОР ВРШИ ДУЖНОСТ ПРОДЕКАНА ЗА НАСТАВУ НА ВОЈНОЈ АКАДЕМИЈИ. У ЗВАЊУ ВАНРЕДНОГ ПРОФЕСОРА НА СПОРТСКОЈ АКАДЕМИЈИ У БЕОГРАДУ РЕАЛИЗУЈЕ НАСТАВУ СОЦИОЛОГИЈЕ СПОРТА. ОБАВЉАО ЈЕ НИЗ НАЈРАЗЛИЧИТИЈИХ ФУНКЦИЈА У ОКВИРУ ВОЈНЕ АКАДЕМИЈЕ И ВИСОКЕ ПОСЛОВНЕ ШКОЛЕ СТРУКОВНИХ СТУДИЈА ИЗ БЕОГРАДА. АУТОР ЈЕ ВЕЋЕГ БРОЈА НАУЧНИХ И СТРУЧНИХ РАДОВА, КАО И ПРОЈЕКАТА И СТУДИЈА. ТАКОЂЕ, ПИСАЦ ЈЕ ВЕЋЕГ БРОЈА КЊИГА ИЗ ОБЛАСТИ ДРУШТВЕНИХ НАУКА.У 2018. ГОДИНИ У SAD СА АСИСТЕНТОМ ДРАГАНОМ ЛАЗИЋЕМ ОБЈАВИО ЈЕ КЊИГУ KРИЗНИ МЕНАЏМЕНТ У АВИЈАЦИЈИ.ОТАЦ ЈЕ ДВОЈЕ ДЕЦЕ.</p>', 1, 'clan', NULL, 40, NULL, NULL),
(16, 'Милосав', 'Стојановић', 'др', 'Професор струковних студија', 'milosav.stojanovic@vpscacak.edu.rs', '165', '1715166937883_693880722.jpg', 'srb', '<p>СТОЈАНОВИЋ МИЛОСАВ ЈЕ РОЂЕН У ПРИШТИНИ, ГДЕ ЈЕ ЗАВРШИО ОСНОВНУ, СРЕДЊУ ШКОЛУ И ЕКОНОМСКИ ФАКУЛТЕТ. МАГИСТРИРАО ЈЕ НА ЕКОНОМСКОМ ФАКУЛТЕТУ У ПРИШТИНИ СА ПРИВРЕМЕНИМ СЕДИШТЕМ У БЛАЦУ 2000.ГОДИНЕ, ОДБРАНОМ ТЕМЕ “МЕЂУНАРОДНО ПЛАЋАЊЕ ПРЕКО КЛИРИНГА” А ДОКТОРСКУ ДИСЕРТАЦИЈУ НА ТЕМУ „СТРАТЕГИЈСКИ МЕНАЏМЕНТ У ЕКОНОМСКИ ЗНАЊА“ ОДБРАНИО ЈЕ 2007 ГОД. НА ФАКУЛТЕТУ ЗА ИНДУСТРИЈСКИ МЕНАЏМЕНТ У КРУШЕВЦУ, УНИВЕРЗИТЕТА УНИОН У БЕОГРАДУ.ОД ФЕБРУАРА 1991. ГОД., ДО КРАЈА 2014. ГОДИНЕ РАДИО У МУП-У РЕПУБЛИКЕ СРБИЈЕ, КАДА ЈЕ И ПЕНЗИОНИСАН, А ПРЕ ТОГА У Т.П. “ДАРДАНИЈА-КОМЕРЦ”, НА ПОЗИЧИЈИ КОМЕРЦИЈАЛНОГ ДИРЕКТОРА.</p><p>ОД ФЕБРУАРА 2015 ГОДИНЕ РАДНУ КАРИЈЕРУ НАСТАВЉА У НАСТАВИ, РАДИ КАО ПРЕДАВАЧ И ПРОДЕКАН ЗА НАСТАВУ НА „ФАКУЛТЕТУ ЗА ЦИВИЛНО ВАЗДУХОПЛОВСТВО“ УИВЕРЗИТЕТА „ЏОН НЕЗБИТ“ У БЕОГРАДУ, ГДЕ ЈЕ ОБАВЉАО И ПОСЛОВЕ ЧЛАНА КОМИСИЈЕ КОНТРОЛЕ КВАЛИТЕТА .</p><p>У ЗВАЊЕ ДОЦЕНТА ИЗАБРАН ЈЕ ФЕБРУАРА 2015. ГОДИНЕ, ГДЕ ЈЕ РАДИО ДО 29.08.2018. ГОДИНЕ ПРЕДМЕТЕ КОЈЕ ЈЕ ПРЕДАВАО СУ: „ОСНОВИ МЕНАЏМЕНТА У ВАЗДУХОПЛОВСТВУ“, „УПРАВЉАЊЕ РИЗИКОМ“ , „МЕНАЏМЕНТ У ВАЗДУХОПЛОВСТВУ“, „ФИНАНСИЈСКИ МЕНАЏМЕНТ“, „МЕНАЏМЕНТ БЕЗБЕДНОСТИ У ВАЗДУХОПЛОВСТВУ“. БИО ЈЕ АНГАЖОВАН И НА „ФАКУЛТЕТУ ЗА ПОСЛОВНЕ СТУДИЈЕ“ У ВРШЦУ НА ПРЕДМЕТУ „ЛОГИСТИКА“ И НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА „ЧАЧАК“- БЕОГРАД, НА ПРЕДМЕТИМА „КОРПОРАТИВНЕ ФИНАНСИЈЕ“ И „БАНКАРСТВО“.</p><p>НАУЧНИМ РАДОМ СЕ БАВИ ОД 2002. ГОДИНЕ И АУТОР ЈЕ ЧЕТИРИ МОНОГРАФИЈЕ И ТРИДЕСЕТАК НАУЧНИХ РАДОВА ОБЈАВЉЕНИХ У МЕЂУНАРОДНИМ И ДОМАЋИМ ЧАСОПИСИМА, КАО И ИЗЛАГАЊА НА МЕЂУНАРОДНИМ И ДОМАЋИМ КОНФЕРЕНЦИЈАМА. </p><p>НАЈЗНАЧАЈНИЈЕ РЕФЕРЕНЦЕ СУ:</p><p> „ПОНАШАЊЕ У ОРГАНИЗАЦИЈАМА” (ЂОРЂЕВИЋ Б., СТОЈАНОВИЋ М.2002), „РАЗМЕРЕ СИВЕ ЕКОНОМИЈЕ И МЕРЕ ЗА ЊЕНО СУЗБИЈАЊЕ“, (СТОЈАНОВИЋ М. 2003), „ЗНАЧАЈ МЕЂУНАРОДНИХ ПЛАЋАЊА ПРЕКО КЛИРИНГА”, ( СТОЈАНОВИЋ М. 2003), „ПОСЕБНЕ ТЕМЕ МЕНАЏМЕНТА”, (ЂОРЂЕВИЋ Б., СТОЈАНОВИЋ М. 2004), „УЛОГА КЛИРИНГА У МЕЂУБАНКАРСКИМ ОБРАЧУНИМА И НЕОПХОДНОСТ ЊЕГОВЕ АФИРМАЦИЈЕ У НАШ ФИНАНСИЈСКИ СИСТЕМ”, (СТОЈАНОВИЋ М.2004), „УНУТРАШЊИ СВЕТ КРЕАТИВНОГ МЕНАЏЕРА“, (ЂОРЂЕВИЋ Б., СТОЈАНОВИЋ М. 2005), „СТРАТЕГИЈА У ЕКОНОМИЈИ ЗНАЊА“, ( СТОЈАНОВИЋ М. 2007), „ЗНАЧАЈ ИНФОРМАЦИОНЕ ТЕХНОЛОГИЈЕ У БАНКАРСТВУ НА СУЗБИЈАЊУ КРИВИЧНОГ ДЕЛА ПРЕВАРЕ КАО САВРЕМЕНОГ ВИДА КРИМИНАЛА ПОД ОКРИЉЕМ „ЛЕГАЛНОГ“ ПОСЛОВАЊА“, ( СТОЈАНОВИЋ М., ПЕТРОВИЋ С. 2010 ), „СТРАТЕГИЈСКИ МЕНАДЗМЕНТ“, (ЂОРЂЕВИЋ. Б, СТОЈАНОВИЋ М. 2013),\'\' ЕФЕКТИ МАРКЕТИНГА ОЛИМПИСКИХ ИГАРА\'\'(М.Р.М.ТРКУЉА:ДР М. СТОЈАНОВИЋ 2017).</p>', 1, NULL, NULL, 41, NULL, NULL),
(17, 'Ненад', 'Дугалић', 'др', 'Професор струковних студија', 'nenad.dugalic@vpscacak.edu.rs', '166', '1715167891485_2486426.jpg', 'srb', '<p class=\"ql-align-justify\">ДР НЕНАД ДУГАЛИЋ, ПРОФЕСОР СТРУКОВНИХ СТУДИЈА, РОЂЕН ЈЕ 19. АПРИЛА 1975. ГОДИНЕ У КРАЉЕВУ, ГДЕ ЈЕ ЗАВРШИО ОСНОВНУ И СРЕДЊУ ЕКОНОМСКУ ШКОЛУ. ДИПЛОМИРАО ЈЕ И МАГИСТРИРАО НА ЕКОНОМСКОМ ФАКУЛТЕТУ УНИВЕРЗИТЕТА У КРАГУЈЕВЦУ, А ДОКТОРИРАО ЈЕ НА ПРАВНОЕКОНОМСКОЈ НАУЧНОЈ ОБЛАСТИ ПРАВНОГ ФАКУЛТЕТА УНИВЕРЗИТЕТА У КРАГУЈЕВЦУ.</p><p class=\"ql-align-justify\">ОДБРАЊЕНА ДОКТОРСКА ДИСЕРТАЦИЈА:</p><p class=\"ql-align-justify\">–&nbsp;&nbsp;&nbsp;НЕНАД ДУГАЛИЋ,<strong>&nbsp;</strong>УЛОГА И ЗНАЧАЈ ЗЕМАЉА БРИКС-А У САВРЕМЕНОЈ ГЛОБАЛНОЈ ЕКОНОМИЈИ, ДОКТОРСКА ДИСЕРТАЦИЈА, ПРАВНИ ФАКУЛТЕТ УНИВЕРЗИТЕТА У КРАГУЈЕВЦУ, 25.06.2020.ГОДИНЕ, М-70.</p><p class=\"ql-align-justify\">ОДБРАЊЕНА МАГИСТАРСКА ТЕЗА:</p><p>–    НЕНАД ДУГАЛИЋ,&nbsp;<em>ЕКОНОМСКИ СУВЕРЕНИТЕТ У УСЛОВИМА ГЛОБАЛИЗАЦИЈЕ</em>, МАГИСТАРСКА ТЕЗА, ЕКОНОМСКИ ФАКУЛТЕТ У КРАГУЈЕВЦУ, 09.11.2009. ГОДИНЕ. [COBISS.SR-ID 512398428],</p><p class=\"ql-align-justify\">&nbsp;</p><p class=\"ql-align-justify\">ОД 2016. ГОДИНЕ ЗАПОСЛЕН ЈЕ НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА – БЕОГРАД, ГДЕ ЈЕ ИЗАБРАН У ЗВАЊЕ ПРЕДАВАЧА А ПОТОМ И У ЗВАЊЕ ПРОФЕСОРА СТРУКОВНИХ СТУДИЈА, ЗА УЖЕ НАУЧНЕ ОБЛАСТИ (1) ЕКОНОМИЈА И (2) ФИНАНСИЈЕ И БАНКАРСТВО.</p><p class=\"ql-align-justify\">ОБЈАВИО ЈЕ ВИШЕ НАУЧНИХ РАДОВА У НАУЧНИМ И СТРУЧНИМ ЧАСОПИСИМА И БИО УЧЕСНИК НАУЧНИХ СКУПОВА И КОНФЕРЕНЦИЈА У ЗЕМЉИ И ИНОСТРАНСТВУ. ТАКОЂЕ, АУТОР ЈЕ И ДВЕ СКРИПТЕ КОЈЕ СЕ КОРИСТЕ У ПРОЦЕСУ НАСТАВЕ.</p><p class=\"ql-align-justify\">СЛУЖИ СЕ ЕНГЛЕСКИМ И РУСКИМ ЈЕЗИКОМ И ПОСЕДУЈЕ ЗНАЊЕ РАДА НА РАЧУНАРУ (WINDOWS, WORD, EXCEL, POWER POINT…).</p><p><br></p>', 1, NULL, NULL, 42, NULL, NULL),
(18, 'Срђан', 'Благојевић', 'др', 'Професор струковних студија', 'srdjan.blagojevic@vpscacak.edu.rs', '167', '1715168310901_22900296.jpg', 'srb', '<p><span style=\"color: black;\">ДР СРЂАН БЛАГОЈЕВИЋ РОЂЕН ЈЕ У БЕОГРАДУ И ИМА ЗВАЊЕ ДОКТОРА НАУКА У ОБЛАСТИ МЕНАЏМЕНТА И БИЗНИСАКАО И МАГИСТРА ИЗ МЕЂУНАРОДНЕ ЕКОНОМИЈЕ, ОБЛАСТ, ПОСЛОВНА ДИПЛОМАТИЈА.</span></p><p><span style=\"color: black;\">НАКОН ДИПЛОМИРАЊА НА САОБРАЋАЈНОМ ФАКУЛТЕТУ У БЕОГРАДУ, ПТТ СМЕРУ, ЗАПОШЉАВА СЕ У ФАБРИЦИ ЗА ПРОИЗВОДЊУ ДИГИТАЛНИХ ТЕЛЕФОНСКИХ ЦЕНТРАЛА „ЕИ ПУПИН ГТЕ“ ГДЕ КАО ИНЖЕЊЕР РАДИ НА КОНФИГУРИСАЊУ И ПРОДАЈИ ДИГИТАЛНИХ ЦЕНТРАЛА ЗА ИНТЕГРИСАНУ КОМУТАЦИЈУ ГОВОРА И ПОДАТАКА. СА МЕСТА РУКОВОДИОЦА ПРОДАЈЕ, 1993. ГОДИНЕ СА ПОРОДИЦОМ ОДЛАЗИ У КАНАДУ ГДЕ СЕ ЗАПОШЉАВА У ЈЕДНОЈ ОД ВОДЕЋИХ КОМПАНИЈА ЗА ПРОИЗВОДЊУ ОПРЕМЕ ЗА ИЗГРАДЊУ МРЕЖА ЗА ПРЕНОС ПОДАТАКА СА ЗАДАТКОМ МЕЂУНАРОДНОГ ШИРЕЊА ПОСЛОВАЊА.</span></p><p><span style=\"color: black;\">2000. ГОДИНЕ ПОСТАЈЕ ПОТПРЕДСЕДНИК ЗА ПРОДАЈУ И МАРКЕТИНГ, ИСТЕ КОМПАНИЈЕ, НА ГЛОБАЛНОМ ТРЖИШТУ. ТОКОМ ГОДИНА ПРОВЕДЕНИХ НА РАДУ У КАНАДИ, ПАРАЛЕЛНО ПОХАЂА КУРСЕВЕ СТРУЧНОГ УСАВРШАВАЊА ИЗ ОБЛАСТИ ИНФОРМАЦИОНЕ ТЕХНОЛОГИЈЕ И ПОСЛОВНОГ ПРЕГОВАРАЊА.</span></p><p><span style=\"color: black;\">2000. ОСНИВА КОМПАНИЈУ „ИНФОРМАЦИОНЕ ТЕХНОЛОГИЈЕ“ КОЈА ЈЕ У ТОМ ТРЕНУТКУ ЛИДЕР У ОБЛАСТИ ИНТЕРНЕТ СЕРВИСА А 2001. ГОДИНЕ, ПО ПОВРАТКУ У СРБИЈУ, БИВА ИЗАБРАН НА МЕСТО ГЕНЕРАЛНОГ ДИРЕКТОРА ЈП „ПТТ СРБИЈА“ И ПРЕДСЕДНИКА УПРАВНОГ ОДБОРА ПОШТАНСКЕ ШТЕДИОНИЦЕ.</span></p><p><span style=\"color: black;\">2003. ГОДИНЕ ПОЧИЊЕ ДА РАЗВИЈА НОВУ КОМПАНИЈУ ЗА РАЗВОЈ И ЕКСПЛОАТАЦИЈУ СМС И ТЕЛЕФОНСКИХ СЕРВИСА И УСЛУГА „СМС НЕТ“ КОЈА ПОСТАЈЕ ПИОНИР У ОБЛАСТИ СМС ГЛАСАЊА НА МЕДИЈИМА. У СКЛОПУ РАДА У КОМПАНИЈИ, A У САРАДЊИ СА КАТЕДРОМ ЗА АКУСТИКУ ЕЛЕКТРОТЕХНИЧКОГ ФАКУЛТЕТА У БЕОГРАДУ, УЧЕСТВУЈЕ У ИЗРАДИ ПАТЕНТА ПОД БРОЈЕМ 49874 „ПРОГРАМ ЗА ДЕТЕКЦИЈУ ЕМОЦИЈА У ЉУДСКОМ ГОВОРУ“.</span></p><p><span style=\"color: black;\">ОД 2011. ГОДИНЕ СТАЛНО ЈЕ ЗАПОСЛЕН НА ВИСОКОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА ИЗ ЧАЧКА ГДЕ ЈЕ ИЗАБРАН У ЗВАЊЕ ПРЕДАВАЧА ИЗ ОБЛАСТИ МЕНАЏМЕНТА И ЕКОНОМИЈЕ.</span></p><p><br></p>', 1, NULL, NULL, 43, NULL, NULL),
(19, 'Наташа', 'Марјановић', 'др', 'Професор струковних студија', 'natasa.marjanovic@vpscacak.edu.rs', '168', '1715168693321_1826585836.jpg', 'srb', '<p>ДР НАТАША МАРЈАНОВИЋ ЈЕ ЗАПОСЛЕНА НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА „ЧАЧАК“ ОД 2015. ГОДИНЕ КАО ПРЕДАВАЧ, А ПОТОМ И КАО ПРОФЕСОР СТРУКОВНИХ СТУДИЈА, ЗА УЖЕ НАУЧНЕ ОБЛАСТИ ЕКОНОМИЈА И ФИНАНСИЈЕ И БАНКАРСТВО.</p><p>НАСТАВНУ АКТИВНОСТ ЗАПОЧЕЛА ЈЕ 2012. ГОДИНЕ НА УНИВЕРЗИТЕТУ ЗА ПОСЛОВНЕ СТУДИЈЕ БАЊА ЛУКА, КАО АСИСТЕНТ ЗА ГРУПУ ЕКОНОМСКИХ ПРЕДМЕТА, А 2017. ГОДИНЕ ЈЕ НА ИСТОМ УНИВЕРЗИТЕТУ ИЗАБРАНА У НАУЧНО-НАСТАВНО ЗВАЊЕ ДОЦЕНТА ЗА УЖЕ НАУЧНЕ ОБЛАСТИ „ТЕОРИЈСКА ЕКОНОМИЈА“ И „ПРЕДУЗЕТНИЧКА ЕКОНОМИЈА“.</p><p>ДОКТОРСКУ ДИСЕРТАЦИЈУ ПОД НАЗИВОМ „САВРЕМЕНИ ПРОБЛЕМИ МЕЂУНАРОДНОГ ФИНАНСИРАЊА ПРИВРЕДНОГ ОПОРАВКА И РАЗВОЈА ЗЕМАЉА У ТРАНЗИЦИЈИ“ ОДБРАНИЛА ЈЕ 28.12.2016. ГОДИНЕ НА ФАКУЛТЕТУ ЗА ПОСЛОВНЕ И ФИНАНСИЈСКЕ СТУДИЈЕ И СТЕКЛА ЈЕ НАУЧНО ЗВАЊЕ ДОКТОР ЕКОНОМСКИХ НАУКА.</p><p>ДР НАТАША МАРЈАНОВИЋ ЈЕ АКТИВАН УЧЕСНИК БРОЈНИХ МЕЂУНАРОДНИХ И НАЦИОНАЛНИХ НАУЧНИХ СКУПОВА И КОНФЕРЕНЦИЈА. АУТОР ЈЕ НЕКОЛИКО НАУЧНИХ РАДОВА, ОБЈАВЉЕНИХ У ДОМАЋИМ И ИНОСТРАНИМ НАУЧНИМ ЧАСОПИСИМА И ЗБОРНИЦИМА СА МЕЂУНАРОДНИХ НАУЧНИХ СКУПОВА. КОАУТОР ЈЕ ДВА УНИВЕРЗИТЕТСКА УЏБЕНИКА.</p><p><br></p>', 1, NULL, NULL, 44, NULL, NULL),
(20, 'Драган', 'Милачић', 'др', 'Професор струковних студија', 'dragan.milacic@vpscacak.edu.rs', '169', '1715169051557_912451987.jpg', 'srb', '<p>1.ОПШТИ ПОДАЦИ:</p><p>• ДАТУМ И МЕСТО РОЂЕЊА: 11.04.1965. ПЕЋ.</p><p>• ДРЖАВЉАНСТВО: РЕПУБЛИКE СРБИЈЕ.</p><p>2. СТЕПЕН ОБРАЗОВАЊА И ЗВАЊА:</p><p>• ДОКТОРАТ: ДОКТОР ЕКОНОМСКИХ НАУКА, ДОКТОРИРАО 12.04.2007. НА ФАКУЛТЕТУ ЗА ТРГОВИНУ И БАНКАРСТВО „ЈАНИЋИЈЕ И ДАНИЦА КАРИЋ“ У БЕОГРАДУ. ДОКТОРСКУ ДИСЕРТАЦИЈУ ПОД НАЗИВОМ „СТРАТЕГИЈА РАЗВОЈА УРБАНОГ ТУРИЗМА У СРБИЈИ“ ОДБРАНИО ПРЕД КОМИСИЈОМ У САСТАВУ: МЕНТОР, ПРОФ. ДР ЖИВОТА РАДОСАВЉЕВИЋ, ЧЛАНОВИ, ПРОФ. ДР БОЖИДАР МИЛЕНОВИЋ И ПРОФ. ДР СНЕЖАНА ШТЕТИЋ.</p><p>• ПОСЛЕДИПЛОМСКЕ СТУДИЈЕ: МАГИСТАР НАУКА О МЕНАЏМЕНТУ, ПОСЛЕДИПЛОМСКЕ СТУДИЈЕ УПИСАО 1997. ГОДИНЕ НА ФАКУЛТЕТУ ЗА МЕНАЏМЕНТ „БРАЋА КАРИЋ“, СМЕР: САОБРАЋАЈ И ВЕЗЕ, ГДЕ ЈЕ И ОДБРАНИО МАГИСТАРСКУ ТЕЗУ У ЈУНУ 2000. ГОДИНЕ ПОД НАЗИВОМ „ УПРАВЉАЊЕ ВЛАСНИЧКО – ОРГАНИЗАЦИОНИМ АСПЕКТИМА ТРАНСФОРМАЦИЈЕ ХОТЕЛСКЕ ИНДУСТРИЈЕ У ЈУГОСЛАВИЈИ ПРЕД КОМИСИЈОМ: МЕНТОР ПРОФ. ДР ЖИВОТА РАДОСАВЉЕВИЋ, ЧЛАНОВИ ПРОФ. ДР ГОРДАНА АЈДУКОВИЋ И ПРОФ. ДР МИЉАН СВОРЦАН.</p><p>• ОСНОВНЕ СТУДИЈЕ: ДИПЛОМИРАНИ ТУРИЗМОЛОГ ПРИРОДНО – МАТЕМАТИЧКИ ФАКУЛТЕТ УНИВЕРЗИТЕТА У БЕОГРАДУ, ОДСЕК ЗА ТУРИЗМОЛОШКЕ НАУКЕ, НА КОЈЕМ ЈЕ ДИПЛОМИРАО 1989. ГОДИНЕ.</p><p>3. ДОСАДАШЊА РАДНА МЕСТА:</p><p>• ОД 2012. – ПРОФЕСОР СТРУКОВНИХ СТУДИЈА НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА „ЧАЧАК“ У БЕОГРАДУ.</p><p>• 2011 – 2012. –ВАНРЕДНИ ПРОФЕСОР НА АЛФА УНИВЕРЗИТЕТУ, БЕОГРАД.</p><p>• 2007 -2011. –ДОЦЕНТ НА ФАКУЛТЕТУ ЗА ТРГОВИНУ И БАНКАРСТВО „ЈАНИЋИЈЕ И ДАНИЦА КАРИЋ“ У БЕОГРАДУ.</p><p>• 2000. – 2007. –АСИСТЕНТ НА ФАКУЛТЕТУ ЗА ТРГОВИНУ И БАНКАРСТВО „ЈАНИЋИЈЕ И ДАНИЦА КАРИЋ“ У БЕОГРАДУ.</p><p>• 1995. – 2000. –САРАДНИК У НАСТАВИ НА АКАДЕМИЈИ ЗА ТРГОВИНУ И БАНКАРСТВО „ЈАНИЋИЈЕ И ДАНИЦА КАРИЋ“ ПЕЋ – БЕОГРАД.</p><p>• 1990. – 1995. –ПРОФЕСОР СТРУЧНИХ ПРЕДМЕТА У СРЕДЊОЈ ЕКОНОМСКОЈ ШКОЛИ У ПЕЋИ.</p><p><br></p>', 1, NULL, NULL, 45, NULL, NULL),
(22, 'Јасмина', 'Радоњић', '', 'Наставник страног језика', 'jasmina.radonjic@vpscacak.edu.rs', '59', '1715169700741_1650314892.jpg', 'srb', '<p>ЈАСМИНА РАДОЊИЋ, ПРОФЕСОР ЕНГЛЕСКОГ ЈЕЗИКА.</p><p>ЈАСМИНА РАДОЊИЋ РОЂЕНА ЈЕ 19.6.1986. У ЧАЧКУ. ПОСЛЕ ЗАВРШЕНЕ ГИМНАЗИЈЕ У ЧАЧКУ, ЗАВРШИЛА ЈЕ ФИЛОЛОШКИ ФАКУЛТЕТ УНИВЕРЗИТЕТА У БЕОГРАДУ, ОДСЕК ЕНГЛЕСКИ ЈЕЗИК И КЊИЖЕВНОСТ.</p><p>ЗАПОСЛЕНА ЈЕ НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА У БЕОГРАДУ ОД 2012. ГОДИНЕ КАО НАСТАВНИК СТРАНОГ ЈЕЗИКА – ПОСЛОВНИ ЕНГЛЕСКИ ЈЕЗИК И ЕНГЛЕСКИ ЗА ЕКОНОМИШТЕ.</p><p>УСАВРШАВАЛА СЕ НА МНОГОБРОЈНИМ КУРСЕВИМА У ЗЕМЉИ И ИНОСТТРАНСТВУ.</p><p><br></p>', 1, NULL, '', 46, NULL, NULL),
(23, 'Соња', 'Жарковић', 'мр', 'Предавач', 'sonja.zarkovic@vpscacak.edu.rs', NULL, '1715597677769_2051811753.jpg', 'srb', '<p>МР СОЊА ЖАРКОВИЋ ЈЕ РОЂЕНА 1978.ГОДИНЕ И ЖИВИ У БЕОГРАДУ. МАГИСТРИРАЛА ЈЕ 2010.ГОДИНЕ НА ФАКУЛТЕТУ ЗА ТРГОВИНУ И БАНКАРСТВО У БЕОГРАДУ, ОБЛАСТ РАЧУНОВОДСТВО И РЕВИЗИЈА. ОД 2002-2015.ГОД.РАДИЛА ЈЕ КАО АСИСТЕНТ НА НАВЕДЕНОМ ФАКУЛТЕТУ. ОД 2016. ГОДИНЕ РАДИ КАО ПРЕДАВАЧ НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА У ЗЕМУНУ, НА РАЧУНОВОДСТВЕНИМ ПРЕДМЕТИМА. АУТОР ЈЕ НЕКОЛИКО РАДОВА ИЗ ОБЛАСТИ РАЧУНОВОДСТВА И РЕВИЗИЈЕ.</p>', 1, NULL, NULL, 47, NULL, NULL),
(24, 'Благоје ', 'Пушоња', 'др', 'Професор струковних студија', 'blagoje.pusonja@vpscacak.edu.rs', '170', '1715170313657_853178646.png', 'srb', '<p>БЛАГОЈЕ ПУШОЊА ЈЕ РОЂЕН 25. МАЈА 1967. ГОДИНЕ У СЕЛУ ВИШЊЕВА, ОПШТИНА СЈЕНИЦА. ОСНОВНУ ШКОЛУ \"29. НОВЕМБАР\" ЗАВРШИО ЈЕ У БАРАМА, ОПШТИНА СЈЕНИЦА. СРЕДЊУ ЕКОНОМСКУ ШКОЛУ (ПРАВНО-БИРОТЕХНИЧКИ СМЕР) ЗАВРШИО ЈЕ У КРАГУЈЕВЦУ 1986. ГОДИНЕ. ПРАВНИ ФАКУЛТЕТ УНИВЕРЗИТЕТА У КРАГУЈЕВЦУ, УПИСАО ЈЕ ШКОЛСКЕ 1986/87. ГОДИНЕ, А ДИПЛОМИРАО 1992. ГОДИНЕ, СА ПРОСЕЧНОМ ОЦЕНОМ 8,39.</p><p>КАО СТУДЕНТ-АПСОЛВЕНТ, КАНДИДАТ БЛАГОЈЕ ПУШОЊА, ЗАПОСЛИО СЕ 1992. ГОДИНЕ У КОМАНДИ КРАГУЈЕВАЧКОГ КОРПУСА ВОЈСКЕ ЈУГОСЛАВИЈЕ, ГДЕ ЈЕ РАДИО ДО 1995. ГОДИНЕ. ОД 15. МАЈА 1995. ГОДИНЕ, РАДИО ЈЕ У ОПШТИНСКОМ СУДУ У СЈЕНИЦИ КАО СУДИЈСКИ ПРИПРАВНИК ДО 10. АВГУСТА 1996. ГОДИНЕ, КАДА ЈЕ ЗАСНОВАО РАДНИ ОДНОС НА НЕОДРЕЂЕНО ВРЕМЕ У МИНИСТАРСТВУ УНУТРАШЊИХ ПОСЛОВА РЕПУБЛИКЕ СРБИЈЕ, СЕКРЕТАРИЈАТ УНУТРАШЊИХ ПОСЛОВА У НОВОМ ПАЗАРУ, ОДЕЉЕЊЕ ПОЛИЦИЈЕ У СЈЕНИЦИ, НА РАДНОМ МЕСТУ ШЕФА ОДСЕКА ЗА СТРАНЦЕ И ПУТНЕ ИСПРАВЕ. ОД 2004. ГОДИНЕ, ПА СВЕ ДО ДАНАС, КАНДИДАТ РАДИ У ПОЛИЦИЈСКОЈ УПРАВИ У КРАГУЈЕВЦУ, У ОДСЕКУ ЗА СУЗБИЈАЊЕ КРИМИНАЛИТЕТА У ПРИВРЕДИ.</p><p>БЛАГОЈЕ ПУШОЊА ЈЕ ДОКТОРСКЕ АКАДЕМСКЕ СТУДИЈЕ УПИСАО ШКОЛСКЕ 2009/10 ГОДИНЕ, НА ПРАВНОМ ФАКУЛТЕТУ УНИВЕРЗИТЕТА У КРАГУЈЕВЦУ. </p><p>ДОКТОРСКУ ДИСЕРТАЦИЈУ ПОД НАЗИВОМ \"ИДЕЈНИ И ПРАВНИ ОКВИР ЗАШТИТЕ РАВНОПРАВНОСТИ ПОЛОВА У УСТАВНОМ ПОРЕТКУ РЕПУБЛИКЕ СРБИЈЕ\", ОДБРАНИО ЈЕ 2014 ГОДИНЕ, УЖА НАУЧНА ОБЛАСТ „ЈАВНО ПРАВО“ (УСТАВНО ПРАВО; РАДНО ПРАВО; УПРАВНО ПРАВО; УВОД У ПРАВО; ПОРОДИЧНО ПРАВО; СОЦИОЛОГИЈА ПРАВА, ОПШТА СОЦИОЛОГИЈА И ДРУГИ СРОДНИ ПРЕДМЕТИ ИЗ НАВЕДЕНЕ НАУЧНЕ ОБЛАСТИ).  </p><p>ПО НАЦИОНАЛНОСТИ СРБИН, ДРЖАВЉАНИН СРБИЈЕ, СА СТАНОМ У КРАГУЈЕВЦУ, УЛИЦА ВЛАДИМИРА РОЛОВИЋ, 7/37.</p><p>РЕГИСТРОВАН У ОРЦИД БАЗИ ИСТРАЖИВАЧА, ПОСЕДУЈЕ ОРЦИД БРОЈ 0000-0001-7696-1113, E-MAIL: BLAGOJEPUSONJA967@GMAIL.COM,  МОБИЛНИ ТЕЛЕФОН 063/619303.</p><p><br></p><p>ДО САДА ЈЕ УЧЕСТВОВАО НА ВЕЛИКОМ БРОЈУ НАУЧНИХ СКУПОВА, ТРИБИНА, ДИСКУСИЈА И СЛИЧНО.</p><p><br></p>', 1, NULL, NULL, 48, NULL, NULL),
(25, 'Иван', 'Ивановић', 'др', 'Професор струковних студија', 'ivan.ivanovic@vpscacak.edu.rs', '171', '1715173366135_976672252.jpg', 'srb', '<p>ДОКТОРСКУ ДИСЕРТАЦИЈУ ПОД НАСЛОВОМ „ЕКОНОМСКИ АСПЕКТИ ИЗГРАДЊЕ ОБНОВЉИВИХ ИЗВОРА ЕНЕРГИЈЕ У ФУНКЦИЈИ ПОВЕЋАЊА КОНКУРЕНТНОСТИ ПРИВРЕДЕ У СРБИЈИ“ ОДБРАНИО ЈЕ 8.3.2013. НА ФАКУЛТЕТУ ЗА МЕНАЏМЕНТ МАЛИХ И СРЕДЊИХ ПРЕДУЗЕЋА У БЕОГРАДУ. ОБЛАСТ: МЕНАЏМЕНТ И БИЗНИС. КОМИСИЈА У САСТАВУ: ПРОФ. ДР БОЖИДАР МИХАЈЛОВИЋ (МЕНТОР), ПРОФ. ДР ДРАГАНА ГЊАТОВИЋ, ПРОФ. ДР ДРАГАН КОСТИЋ.</p><p>2008.ГОДИНЕ УПИСАО ЈЕ ДИПЛОМСКЕ АКАДЕМСКЕ (МАСТЕР) СТУДИЈЕ НА ФАКУЛТЕТУ ЗА ПОСЛОВНЕ СТУДИЈЕ МЕГАТРЕНД УНИВЕРЗИТЕТА У БЕОГРАДУ, СМЕР МЕНАЏМЕНТ И БИНИС. У ТОКУ СТУДИЈА ОСТВАРИЛА ЈЕ ВИСОКЕ РЕЗУЛТАТЕ СА ПРОСЕЧНОМ ОЦЕНОМ 9,25.</p><p>МАСТЕР РАД ПОД НАСЛОВОМ „ЛИДЕРСТВО У САВРЕМЕНИМ КОМПАНИЈАМА СРБИЈЕ СА ОСВРТОМ НА БРАНИЧЕВСКОМ ОКРУГУ” УСПЕШНО ЈЕ ОДБРАНИО НОВЕМБРА 2009. ГОДИНЕ. СМЕР: МЕНАЏМЕНТ И БИЗНИС. КОМИСИЈА У САСТАВУ: ПРОФ.ДР МОМЧИЛО ЖИВКОВИЋ (МЕНТОР) ПРОФ.ДР ДРАГАН КОСТИЋ, ПРОФ ДР ТАТЈАНА ЦВЕТКОВСКИ.</p><p>ДИПЛОМИРАО НА ФАКУЛТЕТУ ЗА ПОСЛОВНЕ СТУДИЈЕ МЕГАТРЕНД УНИВЕРЗИТЕТА У БЕОГРАДУ 2007. ГОДИНЕ.</p><p>ДИПЛОМИРАО НА ВИШОЈ ШКОЛИ ЗА МЕНАЏМЕНТ МЕГАТРЕНД УНИВЕРЗИТЕТА У ЗАЈЕЧАРУ 2005. ГОДИНЕ.</p><p>ГИМНАЗИЈУ У ПОЖАРЕВЦУ, ПРИРОДНО-МАТЕМАТИЧКИ СМЕР, ЗАВРШИО ЈЕ 1994. ГОДИНЕ.</p><p>ОСНОВНУ ШКОЛУ ВЕЉКО ДУГОШЕВИЋ ЗАВРШИО ЈЕ У ПОЖАРЕВЦУ 1990. ГОДИНЕ (ВУКОВА ДИПЛОМА).</p><p><br></p><p>РАДНО ИСКУСТВО:</p><p>ОД ЈУНА 2008. ГОДИНЕ ЈЕ У СТАЛНОМ РАДНОМ ОДНОСУ НА ФАКУЛТЕТУ ЗА ПОСЛОВНЕ СТУДИЈЕ МЕГАТРЕНД УНИВЕРЗИТЕТА У БЕОГРАДУ.</p><p>ОД ЈАНУАРА 2014. РАДИ КАО ДОЦЕНТ НА ПРЕМЕТИМА: МЕНАЏМЕНТ ЉУДСКИХ РЕСУРСА, ПОСЛОВНА КОМУНИКАЦИЈА, ОРГАНИЗАЦИОНО ПОНАСАЊЕ, ИНТЕРКУЛТУРНИ МЕНАЏМЕНТ И УПРАВЉАЊЕ ПРОЈЕКТИМА.</p><p>ОД ЈАНУАРА 2010. РАДИО НА МЕСТУ АСИСТЕНТА НА ПРЕДМЕТИМА: ТРЖИШНА ЕКОНОМИЈА, МОНЕТАРНЕ И ЈАВНЕ ФИНАНСИЈЕ, ПОСЛОВНА КОМУНИКАЦИЈА, МЕЂУНАРОДНИ ЕКОНОМСКИ ОДНОСИ, ОРГАНИЗАЦИОНО ПОНАШАЊЕ, МЕЂУНАРОДНО БАНКАРСТВО, БАНКАРСКО И БЕРЗАНСКО ПОСЛОВАЊЕ НА ОСНОВНИМ И МАСТЕР СТУДИЈАМА.</p><p>РАДИО КАО МЕНАЏЕР ПРОДАЈЕ, ОРГАНИЗАЦИЈЕ И КОМУНИКАЦИЈЕ У КОМПАНИЈАМА: \"DMA COMPUTERS\", \" WIENER-GRAWE \", \" STRATEGIC MARKETING \".</p><p><br></p>', 1, NULL, NULL, 49, NULL, NULL),
(26, 'Радица ', 'Павловић', 'др', 'Професор струковних студија', 'radica.pavlovic@vpscacak.edu.rs', '172', '1715246644498_1066690555.jpg', 'srb', '<p class=\"ql-align-center\">др Радица Ж. Павловић</p><p class=\"ql-align-justify\">	Радица Павловић рођена je 13.08.1980. године у Петровцу на Млави. Основну и средњу школу завршила је у Пожаревцу са одличним успехом. Економски факултет у Нишу завршила је 2004. године одбраном дипломског рада: <em><u>Ортодоксни стабилизациони програми</u></em> и стекла звање дипломирани економиста, а 2009. године завршила је мастер академске студије на Факултету за пословне студије (ФПС), Мегатренд универзитета у Београду одбраном мастер рада: <em><u>Анализа повезаности новчаних токова и резултата пословања</u></em><u> и стекла</u> звање: дипломирани економиста – мастер. Докторске студије завршила 2013. године на Универзитету Привредна академија, Нови Сад, Факултет за менаџмент малих и средњих предузећа, Београд, одбраном докторске дисертације под називом: <em><u>Динамички аспект финансијске анализе на бази токова готовине са посебним освртом на значај њене примене у Републици Србији</u> </em>&nbsp;&nbsp;и стекла звање: доктор наука.</p><p class=\"ql-align-justify\">Своју академску каријеру започела је на&nbsp;на Мегатренд унверзитеу као сарадник у настави, да би 2013. године била изабрана у звање доцента за ужу научну област Економија на Факултету за пословне студије Пожаревац (где је држала предмете: Рачуноводство са ревизијом, Анализа пословања, Инструменти платног промета, Финансијски менаџмент на основним академским студијама и Међународно пословно финансирање на мастер академмским студијама). Од 2014. године запослена је као доцент на Факултету за менаџмет у Зајечару а 2018. године изабрана у звање <u>ванредног професора</u> за ужу област: Економске науке, а за уже научне области: Финансије, банкарство и берзе и Анализа пословања и рачуноводство, где је поред наведених предмета држала и предмете: Корпоративне финансије, Финансирање инвестиција, на основним академским студијама, и, Планирање и анализа трошкова, на мастер академским студијама. Од децембра месеца 2019. године ради на Правном факултету, Мегатренд универзитета у Београду, а од 30.11.2020. године изабрана је за ванредног професора за уже научне области: Трговинскоправна и Правноекономска, где је држала предмете: Економска политика, Јавне финансије и финансијско право, а 2022. године за <u>редовног професора</u> за <strong>научну област</strong>: Економија и где је успешно изводила наставу наведених области. Поред тога била је ментор&nbsp;за израду већег броја завршних радова као и члан комисије истих на основним, мастер и докторским студијама. Од априла месеца 2024.године запослена је као професор на Високој пословној школи струковних студија „Чачак“ у Београду на предметима из области економских наука. </p><p class=\"ql-align-justify\">Била је: Члан Савета Факултета за менаџмент у Зајечару, од 30.10.2014-201., Помоћник руководиоца за наставу на Факултету за менаџмент Зајечар- ВШЈ Пожаревац – од октобра 2014- 03.12.2019., предсдник бројних комисија на факултету, рецезент часописа: Megabiznis, Мегатренд ревија, Зборник радова Географског института „Јован Цвијић” САНУ, Београд., Зборник радова Матице српске за друштвене науке, Нови Сад, Продекан за студије другог и трећег степена и научно-истраживачки рад-од маја 2021-2024., Руководилац научно истраживачкг пројеката Правног факултета Мегатренд универзитета: Будућност пословања у Србији (01.10.2020-30.09.2023)...</p><p class=\"ql-align-justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Током образовне, научне и стручне каријере постигла је више значајнијих резултата, и то: током 1999. и 2000. године учествовала је на републичким смотрама научно-истраживачких радова талената из природних, техничких, друштвених и хуманитарних наука, где је освојила прво место и златну медаљу, као и дипломе за постигнуте резултате из области социологије, од стране <u>Министарства просвете, Министарства за науку и технологију, Српске академије наука и уметности и Републичког центра за таленте из природних и техничких наука;</u> објавила је преко 90 научних и стручних радова како на домаћим и међународним научним конференцијама, тако и у домаћим и иностраним научним часописима, монографские студије, из области економије, финансија и анализе, менаџмента.</p><p><br></p>', 1, NULL, NULL, 50, NULL, NULL),
(27, 'Марија', 'Бешлин-Ферух', 'мр', 'Предавач', 'marija.beslin@vpscacak.edu.rs', '173', '1715246938662_457189832.jpg', 'srb', '<p class=\"ql-align-justify\">Мр Марија Бешлин Ферух дипломирала је и магистрирала на Економском факултету Универзитета у Београду, на смеру Маркетинг. Докторска дисертација на тему “Модалитети комуницирања у концепту директног маркетинга са посебним освртом на банкарски сектор у Србији” прихаћена је на Сенату Алфа БК универзитета. </p><p class=\"ql-align-justify\"><br></p><p class=\"ql-align-justify\">Завршила је семинар у оквиру тренинг програма Пакта за стабилност Југоисточне Европе \"Предузетништво и подстицање развоја малих и средњих предузећа\" на Институту Економских Наука у Београду чиме је стекла сертификат тренера у области подстицања малог бизниса. Учествовала је на пројекту “Effect of Consumer Ethnocentism on Evaluation of Foreign vs. Domestic Brands: A study of Consumer Behavior in Southeast Europe” на Институту Економских Наука у Београду.  Радила је као менаџер за комуникације у маркетиншкој агенцији IntermaNet-Belgrade, Strategy & Marketing Auditing Group од 2006 - 2009. године.</p><p class=\"ql-align-justify\"><br></p><p class=\"ql-align-justify\">Радила је на Факултету за менаџмент, Универзитета \"Браћа Карић\" од 1996-2010. године као асистент на предметима: Међународни маркетинг, Маркетинг менаџмент, Менаџмент малих и средњих предузећа, Маркетинг истраживање, Понашање потрошача. На Факултету за економију и политичке науке Алфа БК Универзитета од 2010-2016. године радила је као асистент на предметима: Принципи маркетинга, Међународни менаџмент, Основи комуникације, Истраживање тржишта.</p><p class=\"ql-align-justify\"><br></p><p class=\"ql-align-justify\">Од 2016. године ради у Високој пословној школи струковних студија “Чачак“. Ангажована је као предавач на предметима: Принципи маркетинга, Понашање потрошача, Маркетинг у трговини. Од 2016-2021. године била је ангажована као предавач на специјалистичким студијама на предмету Маркетинг менаџмент. </p><p class=\"ql-align-justify\"><br></p><p class=\"ql-align-justify\">Објавила је више научних и стручних радова из области маркетинга и менаџмента.</p><p><br></p>', 1, NULL, NULL, 51, NULL, NULL),
(28, 'Срђан', 'Миленковић', 'др', 'Професор струковних студија', 'srdjan.milenkovic@vpscacak.edu.rs', '174', '1715248133204_1252579628.jpg', 'srb', '<p>ДР СРЂАН МИЛЕНКОВИЋ МАГИСТАРСКУ ТЕЗУ ПОД НАЗИВОМ „МОГУЋНОСТ ПРОЈЕКТОВАЊА ОРГАНИЗАЦИОНОГ МОДЕЛА ОПЕРАТИВНОГ НИВОА ВОЈСКЕ СРБИЈЕ“ ОДБРАНИО ЈЕ НА ВОЈНОЈ АКАДЕМИЈИ, А ДОКТОРСКУ ДИСЕРТАЦИЈУ ПОД НАЗИВОМ „ЕКОНОМИЈА ХУМАНОГ КАПИТАЛА И ЕДУКАТИВНИ МЕНАЏМЕНТ“ ОДБРАНИО ЈЕ НА УНИВЕРЗИТЕТУ ЗА ПОСЛОВНЕ СТУДИЈЕ У БАЊА ЛУЦИ. </p><p>У ЗВАЊЕ ДОЦЕНТА ЗА УЖЕ НАУЧНЕ ОБЛАСТИ МЕНАЏМЕНТ И ПРЕДУЗЕТНИЧКА ЕКОНОМИЈА ИЗАБРАН НА УНИВЕРЗИТЕТУ ЗА ПОСЛОВНЕ СТУДИЈЕ У БАЊА ЛУЦИ 2014. ГОДИНЕ, А У ЗВАЊЕ ПРОФЕСОРА СТРУКОВНИХ СТУДИЈА ЗА УЖЕ НАУЧНЕ ОБЛАСТИ МЕНАЏМЕНТ И МАРКЕТИНГ У ВИСОКОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА „ЧАЧАК“ 2017. ГОДИНЕ.</p><p><br></p>', 1, NULL, NULL, 52, NULL, NULL),
(29, 'Милан', 'Шошкић', 'мр', 'Предавач', 'milan.soskic@vpscacak.edu.rs', NULL, '1715248513747_242126388.jpg', 'srb', '<p>ШОШКИЋ МР МИЛАН</p><p>ДИПЛОМА – ЕКОНМОСКИ ФАКУЛТЕТ, УНИВЕРЗИТЕТ У ПРИШТИНИ.</p><p>МАГИСТРАТУРА – ЕКОНОМСКИ ФАКУЛТЕТ, УНИВЕРЗИТЕТ У ПРИШТИНИ СА СЕДИШТЕМ У К. МИТРОВИЦИ.</p><p>АУТОР ВИШЕ НАУЧНИХ РАДОВА, У ТОКУ ИЗРАДА ДОКТОРСКЕ ДИСЕРТАЦИЈЕ НА ФАКУЛТЕТУ БЕОГРАДСКА БАНКАРСКА АКАДЕМИЈА.</p><p><br></p>', 1, NULL, NULL, 53, NULL, NULL),
(30, 'Горан', 'Славковић', 'др', 'Професор струковних студија', 'goran.slavkovic@vpscacak.edu.rs', '175', '1715248652917_439433344.jpg', 'srb', '<p>РОЂЕН У БЕОГРАДУ, СРБИЈА, 1967. ГОДИНЕ. ЗАВРШИО МАШИНСКИ  ФАКУЛТЕТ УНИВЕРЗИТЕТА У БЕОГРАДУ, КАТЕДРА ЗА ПРОИЗВОДНО МАШИНСТВО, 1993. ГОДИНЕ И ЕЛЕКТРОТЕХНИЧКИ ФАКУЛТЕТ УНИВЕРЗИТЕТА У БЕОГРАДУ, КАТЕДРА ЗА РАЧУНАРСКУ ТЕХНИКУ И ИНФОРМАТИКУ, 2004. ГОДИНЕ. МАГИСТРИРАО ЈЕ НА МАШИНСКОМ  ФАКУЛТЕТУ УНИВЕРЗИТАТА У БЕОГРАДУ 2001. ГОДИНЕ (КАТЕДРА ЗА ПРОИЗВОДНО МАШИНСТВО, СМЕР ФЛЕКСИБИЛНЕ ПРОИЗВОДНЕ ТЕХНОЛОГИЈЕ И РОБОТИКА). ДОКТОРИРАО ЈЕ 2011. ГОДИНЕ ОДБРАНОМ ДОКТОРСКЕ ДИСЕРТАЦИЈЕ НА МАШИНСКОМ ФАКУЛТЕТУ УНИВЕРЗИТЕТА У БЕОГРАДУ.</p><p>ТОКОМ СВОЈЕ КАРИЈЕРЕ РАДИО ЈЕ НА МАШИНСКОМ ФАКУЛТЕТУ УНИВЕРЗИТЕТА У БЕОГРАДУ, НА КАТЕДРИ ЗА ПРОИЗВОДНО МАШИНСКТВО, ОД 1993. ГОДИНЕ. ОД 2006. ДО 2008. ГОДИНЕ ЈЕ БИО НА СТРУЧНОМ УСАВРШАВАЊУ У ЈАПАНУ (НАГОYА УНИВЕРСИТY, ГРАДУАТЕ СЦХООЛ ОФ ИНФОРМАТИОН СЦИЕНЦЕ, ДЕПАРТМЕНТ ОФ ЦОМПЛЕЏ СYСТЕМС) РАДИ ИЗРАДЕ ДОКТОРСКЕ ТЕЗЕ, ГДЕ ЈЕ УЧЕСТВОВАО У НАУЧНО-ИСТРАЖИВАЧКИМ И НАСТАВНИМ АКТИВНОСТИМА. У ПЕРИОДУ ОД 2013. ДО 2014. ГОДИНЕ ПОЗВАН ЈЕ У МЕКСИКО КАО ГОСТУЈУЋИ ПРОФЕСОР НА ИНСТИТУТО ТЕЦНОЛОГИЦО ДЕ МОНТЕРРЕY, МЕЏИЦО ЦИТY, ГДЕ ЈЕ У ДВА СЕМЕСТРА ШКОЛСКЕ 2013/2014. ГОДИНЕ ДРЖАО НАСТАВУ НА ВИШЕ ПРЕДМЕТА НА ДЕПАРТАМЕНТО МЕЦАТРОНИЦА И ДЕПАРТАМЕНТО ИНГЕНЕРИА ИНДУСТРИАЛ. КАО ДОЦЕНТ НА ФАКУЛТЕТУ ЗА КОМПЈУТЕРСКЕ НАУКЕ МЕГАТРЕНД УНИВЕРЗИТЕТА (ОБЛАСТ РАЧУНАРСТВО), ОД 2014. ГОДИНЕ, УЧЕСТВУЈЕ У НАСТАВНИМ АКТИВНОСТИМА (ФАКУЛТЕТ ЗА КОМПЈУТЕРСКЕ НАУКЕ, ПРАВНИ ФАКУЛТЕТ, ФАКУЛТЕТ ЗА КУЛТУРУ И МЕДИЈЕ, ФАКУЛТЕТ ЗА ЦИВИЛНО ВАЗДУХОПЛОВСТВО, ГЕОЕКОНОМСКИ ФАКУЛТЕТ, ФАКУЛТЕТ ЗА ПОСЛОВНЕ СТУДИЈЕ БЕОГРАД, ФАКУЛТЕТ ЗА ПОСЛОВНЕ СТУДИЈЕ ВРШАЦ, ФАКУЛТЕТ ЗА ЕКОНОМСКО ПОСЛОВАЊЕ ВАЉЕВО) И ДРЖИ НАСТАВУ И ИСПИТЕ НА ОСНОВНИМ И МАСТЕР АКАДЕМСКИМ СТУДИЈАМА. НА ФАКУЛТЕТУ ЗА ЦИВИЛНО ВАЗДУХОПЛОВСТВО ЈЕ БИО АНГАЖОВАН ОД 2017. ГОДИНЕ ДО 2022. ГОДИНЕ. НА ФАКУЛТЕТУ ЗА КОМПЈУТЕРСКЕ НАУКЕ МЕГАТРЕНД УНИВЕРЗИТЕТА ОБАВЉАО ФУНКЦИЈУ ПРОДЕКАНА ЗА НАСТАВУ НА ОСНОВНИМ АКАДЕМСКИМ СТУДИЈАМА, ОД 2017. ГОДИНЕ. ОД 2022. ГОДИНЕ ЗАСНИВА РАДНИ ОДНОС НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА У БЕОГРАДУ, ГДЕ ЈЕ ИЗАБРАН КАО ПРОФЕСОР ЗА УЖЕ НАУЧНЕ ОБЛАСТИ ИНФОРМАТИКА И РАЧУНАРСКЕ ТЕХНОЛОГИЈЕ, И ДРЖИ НАСТАВУ НА ПРЕДМЕТИМА ПОСЛОВНА СТАТИСТИКА И КВАНТИТАТИВНЕ МЕТОДЕ. У ВИШЕ НАВРАТА БИО ЈЕ ЧЛАН КОМИСИЈЕ ЗА ОДБРАНУ ЗАВРШНИХ И ДИПЛОМСКИХ РАДОВА НА МАШИНСКОМ ФАКУЛТЕТУ УНИВЕРЗИТЕТА У БЕОГРАДУ. ТАКОЂЕ, НА МЕГАТРЕНД УНИВЕРЗИТЕТУ ЈЕ БИО ЧЛАН КОМИСИЈЕ И МЕНТОР ЗА ОДБРАНУ ЗАВРШНИХ, ДИПЛОМСКИХ И ДОКТОРСКИХ РАДОВА. ОБЈАВИО ЈЕ ПРЕКО 30 РАДОВА НАУЧНО-СТРУЧНОГ КАРАКТЕРА, И РАДИО НА НИЗ НАУЧНО-ИСТРАЖИВАЧКИХ ПРОЈЕКАТА. УЧЕСТВОВАО ЈЕ НА ОРГАНИЗАЦИЈИ И ОДРЖАВАЊУ ДОМАЋИХ И МЕЂУНАРОДНИХ КОНФЕРЕНЦИЈА У СВОЈСТВУ ЧЛАНА ОРГАНИЗАЦИОНОГ ОДБОРА, КАО И УРЕДНИКА ЗБОРНИКА РАДОВА.</p><p><br></p>', 1, NULL, NULL, 54, NULL, NULL),
(31, 'Саша', 'Стефановић', 'мр', 'Предавач', 'sasa.stefanovic@vpscacak.edu.rs', '176', '1715248825153_1667443123.jpg', 'srb', '<p>МР САША СТЕФАНОВИЋ, ПРЕДАВАЧ</p><p>ДИПЛОМИРАО ЈЕ НА ВОЈНОЈ АКАДЕМИЈИ. АКАДЕМСКИ НАЗИВ МАГИСТАР ЕКОНОМСКИХ НАУКА СТЕКАО ЈЕ НА ЕКОНОМСКОМ ФАКУЛТЕТУ УНИВЕРЗИТЕТА У БЕОГРАДУ.</p><p>КАО ДОБИТНИК СТИПЕНДИЈЕ ЕВРОПСКЕ КОМИСИЈЕ УСАВРШАВАО СЕ НА УНИВЕРЗИТЕТУ У НИЦИ-SOPHIA ANTIPOLIS У ФРАНЦУСКОЈ.</p><p>БИО ЈЕ ЗАПОСЛЕН МИНИСТАРСТВУ ОДБРАНЕ, ИНСТИТУТУ ЕКОНОМСКИХ НАУКА, БЕОГРАДСКОЈ БАНКАРСКОЈ АКАДЕМИЈИ (НЕПУНО РАДНО ВРЕМЕ), ПРЕДУЗЕЋУ GMS INTERNATIONAL Д.О.О И ИНСТИТУТУ ЗА НЕКРЕТНИНЕ Д.О.О. ИЗ БЕОГРАДА ЧИЈИ ЈЕ ОСНИВАЧ И ДИРЕКТОР. ОД 2016. ГОДИНЕ ЗАПОСЛЕН ЈЕ, КАО ПРЕДАВАЧ, У ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА „ЧАЧАК“.</p><p>БИРАН ЈЕ У НАСТАВНА ЗВАЊА АСИСТЕНТ-ПРИПРАВНИК, АСИСТЕНТ И ПРЕДАВАЧ, КАО И ИСТРАЖИВАЧКА ЗВАЊА ИСТРАЖИВАЧ-ПРИПРАВНИК И ИСТРАЖИВАЧ-САРАДНИК.</p><p>КАО АСИСТЕНТ ИЛИ ПРЕДАВАЧ, У КОНТИНУИТЕТУ ЈЕ БИО АНГАЖОВАН У НАСТАВИ, (КАО ЗАПОСЛЕНИ СА ПУНИМ ИЛИ НЕПУНИМ РАДНИМ ВРЕМЕНОМ, ПО УГОВОРУ О ДЕЛУ ИЛИ КАО ГОСТУЈУЋИ НАСТАВНИК) У ВИШЕ НАСТАВНИХ ИНСТИТУЦИЈА, ИЗ ОБЛАСТИ ЕКОНОМИЈЕ, ФИНАНСИЈА, БАНКАРСТВА, ИНВЕСТИЦИЈА И МЕНАЏМЕНТА.</p><p>КАО ПРЕДАВАЧ У ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА „ЧАЧАК“ РЕАЛИЗУЈЕ НАСТАВНЕ АКТИВНОСТИ ИЗ НАУЧНИХ ОБЛАСТИ ЕКОНОМИЈА И ФИНАНСИЈЕ И БАНКАРСТВО</p><p>АНГАЖОВАН ЈЕ НА ВЕЋЕМ БРОЈУ СЕМИНАРА И КУРСЕВА ЗА ПОТРЕБЕ МЕЂУНАРОДНИХ И ДОМАЋИХ ИНСТИТУЦИЈА. УЧЕСТВОВАО ЈЕ У РЕАЛИЗАЦИЈИ НАУЧНИХ ПРОЈЕКАТА ПОДРЖАНИХ И ФИНАНСИРАНИХ ОД СТРАНЕ МИНИСТАРСТВА ЗАДУЖЕНОГ ЗА ПОСЛОВЕ НАУКЕ И МЕЂУНАРОДНИХ ОРГАНИЗАЦИЈА И ВИШЕ ДЕСЕТИНА КОМЕРЦИЈАЛНИХ ПРОЈЕКАТА НАРУЧЕНИХ ОД СТРАНЕ МИНИСТАРСТАВА, СТРАНИХ ИЛИ ДОМАЋИХ КОМПАНИЈА.</p><p>ИМЕНОВАН ЈЕ ЗА СТАЛНОГ СУДСКОГ ВЕШТАКА ЕКОНОМСКО-ФИНАНСИЈСКЕ СТРУКЕ.</p><p>АУТОР ЈЕ ИЛИ КОАУТОР ТРИДЕСЕТАК НАУЧНИХ РАДОВА ОБЈАВЉЕНИХ У НАУЧНИМ ЧАСОПИСИМА И ЗБОРНИЦИМА СА НАУЧНИХ КОНФЕРЕНЦИЈА, ИЛИ, ПАК, ПРЕДСТАВЉАЈУ ПОГЛАВЉЕ У МОНОГРАФИЈИ МЕЂУНАРОДНОГ ИЛИ НАЦИОНАЛНОГ ЗНАЧАЈА ИЛИ НАУЧНУ МОНОГРАФИЈУ.</p><p>ГОВОРИ ЕНГЛЕСКИ ЈЕЗИК, А ПОЗНАЈЕ ОСНОВЕ РУСКОГ И ФРАНЦУСКОГ ЈЕЗИКА.</p><p><br></p>', 1, NULL, NULL, 55, NULL, NULL),
(32, 'Саво', 'Радоњић', 'др', 'Професор струковних студија', 'savo.radonjic@vpscacak.edu.rs', NULL, '1716983285863_814375018.jpg', 'srb', '<p>САВО РАДОЊИЋ РОЂЕН ЈЕ 20.09.1975. ГОДИНЕ У ПЕЋИ.</p><p>ОБРАЗОВАЊЕ:</p><p class=\"ql-align-justify\">МАСТЕР ПРАВНИК – ОБЛАСТ: ТЕОРИЈСКО ПРАВНА, МАСТЕР РАД: ЈЕЗИЧКО ТУМАЧЕЊЕ ПРАВА, ПРАВНИ ФАКУЛТЕТ УНИВЕРЗИТЕТА У КРАГУЈЕВЦУ, 05.02.2019.</p><p class=\"ql-align-justify\">ДИПЛОМИРАНИ ПРАВНИК – ОБЛАСТ: ОПШТЕ ПРАВО, ПРАВНИ ФАКУЛТЕТ ЗА ПРИВРЕДУ И ПРАВОСУЂЕ, УНИВЕРЗИТЕТ ПРИВРЕДНА АКАДЕМИЈА У НОВОМ САДУ, 16.06.2017.</p><p class=\"ql-align-justify\">ДОКТОР ЕКОНОМСКИХ НАУКА, ДОКТОРСКА ДИСЕРТАЦИЈА: \'\'ТРГОВИНСКЕ ДЕТЕРМИНАНТЕ ПРИДРУЖИВАЊА ЗЕМАЉА ЗАПАДНОГ БАЛКАНА ЕВРОПСКОЈ УНИЈИ – ПЕРСПЕКТИВА СРБИЈЕ\'\', УНИВЕРЗИТЕТ \'\'БРАЋА КАРИЋ\'\', ФАКУЛТЕТ ЗА ТРГОВИНУ И БАНКАРСТВО, 02.07.2008.</p><p class=\"ql-align-justify\">МАГИСТАР НАУКА О МЕНАЏМЕНТУ - ОБЛАСТ: МЕНАЏМЕНТ У ТРГОВИНИ, МАГИСТАРСКИ РАД: \'\'ТОКОВИ И МОДАЛИТЕТИ СПОЉНОТРГОВИНСКЕ РОБНЕ РАЗМЕНЕ СРБИЈЕ И ЦРНЕ ГОРЕ СА ЗЕМЉАМА ЈУГОИСТОЧНЕ ЕВРОПЕ\'\', УНИВЕРЗИТЕТ \'\'БРАЋА КАРИЋ\'\' ФАКУЛТЕТ ЗА ТРГОВИНУ И БАНКАРСТВО, 12. 12. 2005.</p><p class=\"ql-align-justify\">ДИПЛОМИРАНИ ЕКОНОМИСТА, ДИПЛОМСКИ РАД: \'\'МЕСТО И УЛОГА ПСИХОЛОГИЈЕ У ЕКОНОМСКОЈ ПРОПАГАНДИ\'\', УНИВЕРЗИТЕТ \'\'БРАЋА КАРИЋ\'\', ФАКУЛТЕТ ЗА ТРГОВИНУ И БАНКАРСТВО, 29. 12. 1997.</p><p>ГИМНАЗИЈА \'\'СВЕТИ САВА\'\', ПЕЋ, 06. 1993.</p><p>УСАВРШАВАЊА:</p><p>ПРАВОСУДНИ ИСПИТ, МИНИСТАРСТВО ПРАВДЕ РЕПУБЛИКЕ СРБИЈЕ  (08.07.2022.)</p><p class=\"ql-align-justify\">РЕЛЕВАНТНО РАДНО И РУКОВОДЕЋЕ ИСКУСТВО:</p><p class=\"ql-align-justify\">1.10.1999. ГОДИНЕ ЗАСНОВАО ЈЕ РАДНИ ОДНОС НА ФАКУЛТЕТУ ЗА ТРГОВИНУ И БАНКАРСТВО, УНИВЕРЗИТЕТ \'\'БРАЋА КАРИЋ\'\';</p><p class=\"ql-align-justify\">1.10.2010. ГОДИНЕ ЗАСНОВАО ЈЕ РАДНИ ОДНОС ЈЕ НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА \'\'ЧАЧАК\'\', СА СЕДИШТЕМ НА АДРЕСИ ГРАДСКИ ПАРК 2 У БЕОГРАДУ;</p><p class=\"ql-align-justify\">У ТОКУ 2009. ГОДИНЕ ОБАВЉАО ЈЕ ДУЖНОСТ ПРОДЕКАНА ЗА НАСТАВУ И НАУЧНО-ИСТРАЖИВАЧКИ РАД НА ФАКУЛТЕТУ ЗА ТРГОВИНУ И БАНКАРСТВО УНИВЕРЗИТЕТА \'\'БРАЋА КАРИЋ\'\';</p><p class=\"ql-align-justify\">ОД 1.12.2011. ГОДИНЕ НАЛАЗИ СЕ НА ФУНКЦИЈИ ДИРЕКТОРА ВИСОКЕ ПОСЛОВНЕ ШКОЛЕ СТРУКОВНИХ СТУДИЈА \'\'ЧАЧАК\'\';</p><p class=\"ql-align-justify\">РУКОВОДИЛАЦ ПРОЈЕКТА: \'\'АНАЛИЗА ЕНЕРГЕТСКЕ ЕФИКАСНОСТИ ПРИВРЕДНИХ СУБЈЕКАТА СА ТЕРИТОРИЈЕ ГРАДА НОВОГ САДА\'\', ГРАДСКА УПРАВА ЗА ЗАШТИТУ ЖИВОТНЕ СРЕДИНЕ ГРАДА НОВОГ САДА, 2019. ГОДИНА;</p><p class=\"ql-align-justify\">РУКОВОДИЛАЦ ПРОЈЕКТА: \'\'АНАЛИЗА И ПОПИС ДИВЉИХ ДЕПОНИЈА НА ТЕРИТОРИЈИ ГРАДА НОВОГ САДА СА АНАЛИЗОМ ТРОШКОВА ЗА САНАЦИЈУ СА ПРЕДЛОГОМ МЕРА ЗА СМАЊЕЊЕ НАСТАЈАЊА ИСТИХ\'\', ГРАДСКА УПРАВА ЗА ЗАШТИТУ ЖИВОТНЕ СРЕДИНЕ ГРАДА НОВОГ САДА, 2020. ГОДИНА;</p><p class=\"ql-align-justify\">ЧЛАН СЕКТОРСКОГ ВЕЋА ЗА СЕКТОР ПОСЛОВНЕ АДМИНИСТРАЦИЈЕ ПРИ АГЕНЦИЈИ ЗА КВАЛИФИКАЦИЈЕ РЕПУБЛИКЕ СРБИЈЕ, ИМЕНОВАН ОДЛУКОМ ВЛАДЕ Р. СРБИЈЕ (СЛ. ГЛАСНИК РС 3/2024 ОД 12.1.2024).</p><p class=\"ql-align-justify\">ИЗБОРИ У ЗВАЊА:</p><p class=\"ql-align-justify\">1.10.1999. ГОДИНЕ ИЗАБРАН ЈЕ ЗА АСИСТЕНТА-ПРИПРАВНИКА ЗА УЖУ НАУЧНУ ОБЛАСТ \'\'ТРГОВИНА\'\', ФАКУЛТЕТ ЗА ТРГОВИНУ И БАНКАРСТВО, УНИВЕРЗИТЕТ \'\'БРАЋА КАРИЋ\'\';</p><p class=\"ql-align-justify\">20. 6. 2006. ГОДИНЕ ИЗАБРАН ЈЕ ЗА САРАДНИКА У ЗВАЊУ АСИСТЕНТА ЗА УЖУ НАУЧНУ ОБЛАСТ \'\'ТРГОВИНА\'\', ФАКУЛТЕТ ЗА ТРГОВИНУ И БАНКАРСТВО, УНИВЕРЗИТЕТ \'\'БРАЋА КАРИЋ\'\';</p><p class=\"ql-align-justify\">17. 9. 2008. ГОДИНЕ ИЗАБРАН ЈЕ У ЗВАЊЕ ДОЦЕНТА ЗА УЖУ НАУЧНУ ОБЛАСТ \'\'ТРГОВИНА\'\',ФАКУЛТЕТ ЗА ТРГОВИНУ И БАНКАРСТВО; УНИВЕРЗИТЕТ \'\'БРАЋА КАРИЋ\'\';</p><p class=\"ql-align-justify\">1.10.2010. ИЗАБРАН ЈЕ У У ЗВАЊЕ ПРОФЕСОРА СТРУКОВНИХ СТУДИЈА ЗА УЖЕ НАУЧНЕ/СТРУЧНЕ ОБЛАСТИ \'\'ОПШТА ЕКОНОМИЈА И ПРИВРЕДНИ РАЗВОЈ\'\' И \'\'МЕНАЏМЕНТ, МАРКЕТИНГ И САВРЕМЕНО ПРЕДУЗЕТНИШТВО\'\' НА ВИСОКОЈ ПОСЛОВНОЈ ШКОЛИ СТРУКОВНИХ СТУДИЈА \'\'ЧАЧАК\'\'.</p><p class=\"ql-align-justify\">ВОЛОНТЕРСКЕ АКТИВНОСТИ:</p><p class=\"ql-align-justify\">ПРЕДСЕДНИК УДРУЖЕЊА ГРАЂАНА: \'\'ЗАВИЧАЈНИ КЛУБ ПЕЋАНАЦА У БЕОГРАДУ\'\', 24.5.2016 – 9.12.2022. ГОДИНЕ;</p><p class=\"ql-align-justify\">ОД 16.1.2023. ГОДИНЕ САВЕТНИК ЈЕ ЗА ПРАВНА ПИТАЊА И ОДНОСЕ С ЈАВНОШЋУ СРПСКЕ ПРАВОСЛАВНЕ ЦРКВЕНЕ ОПШТИНЕ ПЕЋКЕ – ПЕЋ, У САСТАВУ ЕПАРХИЈЕ РАШКО-ПРИЗРЕНСКЕ.</p><p><br></p>', 1, NULL, NULL, 56, NULL, NULL),
